(1, 1, 'ar', 'New User', '<p>&nbsp;<br>   {app_name}.</p><p><b>  </b>: {email}<br><b> </b> : {password}</p><p>{app_url}</p><p><br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(2, 1, 'da', 'New User', '<p>Hej,&nbsp;<br>Velkommen til {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Adgangskode</b> : {password}</p><p>{app_url}</p><p>Tak,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(3, 1, 'de', 'New User', '<p>Hallo,&nbsp;<br>Willkommen zu {app_name}.</p><p><b>Email </b>: {email}<br><b>Passwort</b> : {password}</p><p>{app_url}</p><p>Vielen Dank,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(4, 1, 'en', 'New User', '<p>Hello,&nbsp;<br>Welcome to {app_name}.</p><p><b>Email </b>: {email}<br><b>Password</b> : {password}</p><p>{app_url}</p><p>Thanks,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(5, 1, 'es', 'New User', '<p>Hola,&nbsp;<br>Bienvenido a {app_name}.</p><p><b>Correo electrnico </b>: {email}<br><b>Contrasea</b> : {password}</p><p>{app_url}</p><p>Gracias,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(6, 1, 'fr', 'New User', '<p>Bonjour,&nbsp;<br>Bienvenue  {app_name}.</p><p><b>Email </b>: {email}<br><b>Mot de passe</b> : {password}</p><p>{app_url}</p><p>Merci,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(7, 1, 'it', 'New User', '<p>Ciao,&nbsp;<br>Benvenuto a {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Parola d\'ordine</b> : {password}</p><p>{app_url}</p><p>Grazie,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(8, 1, 'ja', 'New User', '<p>&nbsp;<br> {app_name}.</p><p><b>E </b>: {email}<br><b></b> : {password}</p><p>{app_url}</p><p><br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(9, 1, 'nl', 'New User', '<p>Hallo,&nbsp;<br>Welkom bij {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Wachtwoord</b> : {password}</p><p>{app_url}</p><p>Bedankt,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(10, 1, 'pl', 'New User', '<p>Witaj,&nbsp;<br>Witamy w {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Haso</b> : {password}</p><p>{app_url}</p><p>Dziki,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(11, 1, 'ru', 'New User', '<p>,&nbsp;<br>   {app_name}.</p><p><b>  </b>: {email}<br><b></b> : {password}</p><p>{app_url}</p><p>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(12, 1, 'pt', 'New User', '<p>Ol,<br>Bem-vindo ao {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Senha</b> : {password}</p><p>{app_url}</p><p>Obrigada,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(13, 2, 'ar', 'New Client', '<p> { client_name }  </p><p>  Client ..</p><p>  : { client_email } </p><p>  : { client_password }</p><p>{ app_url }</p><p></p><p>{ app_name }</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(14, 2, 'da', 'New Client', '<p>Hej { client_name },</p><p> Du er nu klient ..</p><p>E-mail: { client_email } </p><p>Password: { client_password }</p><p>{ app_url }</p><p>Tak.</p><p>{ app_name }</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(15, 2, 'de', 'New Client', '<p>Hallo {client_name}, </p><p>Sie sind jetzt Client ..</p><p>E-Mail: {client_email}</p><p> Kennwort: {client_password}</p><p>{app_url}</p><p>Danke,</p><p>{Anwendungsname}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(16, 2, 'en', 'New Client', '<p><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">Hello {client_name},</span><br style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\"><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">You are now Client..</span><br style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\"><b data-stringify-type=\"bold\" style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">Email&nbsp;</b><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">: {client_email}</span><br style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\"><b data-stringify-type=\"bold\" style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">Password</b><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">&nbsp;: {client_password}</span><br style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\"><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">{app_url}</span><br style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\"><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">Thanks,</span><br style=\"box-sizing: inherit; color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\"><span style=\"color: rgb(29, 28, 29); font-family: Slack-Lato, Slack-Fractions, appleLogo, sans-serif; font-size: 15px; font-variant-ligatures: common-ligatures; background-color: rgb(248, 248, 248);\">{app_name}</span><br></p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(17, 2, 'es', 'New Client', '<p>Hola {nombre_cliente},</p><p> ahora es Cliente ..</p><p>Correo electrnico: {client_email}</p><p> Contrasea: {client_password}</p><p>{app_url}</p><p>Gracias,</p><p>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(18, 2, 'fr', 'New Client', '<p>Bonjour { client_name }, </p><p>Vous tes maintenant Client ..</p><p>Adresse lectronique: { client_email } </p><p>Mot de passe: { client_password }</p><p>{ app_url }</p><p>Merci,</p><p>{ app_name }</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(19, 2, 'it', 'New Client', '<p>Hello {client_name}, </p><p>Tu ora sei Client ..</p><p>Email: {client_email} </p><p>Password: {client_password}</p><p>{app_url}</p><p>Grazie,</p><p>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(20, 2, 'ja', 'New Client', '<p> {client_name} </p><p></p><p>E  : {client_email}</p><p>  : {client_password}</p><p>{app_url}</p><p></p><p>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(21, 2, 'nl', 'New Client', '<p>Hallo { client_name }, </p><p>U bent nu Client ..</p><p>E-mail: { client_email } </p><p>Wachtwoord: { client_password }</p><p>{ app_url }</p><p>Bedankt.</p><p>{ app_name }</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(22, 2, 'pl', 'New Client', '<p>Witaj {client_name }, </p><p>jeste teraz Client ..</p><p>E-mail: {client_email }</p><p> Haso: {client_password }</p><p>{app_url }</p><p>Dzikuj,</p><p>{app_name }</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(23, 2, 'ru', 'New Client', '<p>Hello { client_name }, </p><p>   ..</p><p>  : { client_email } </p><p>: { client_password }</p><p>{ app_url }</p><p>.</p><p>{ app_name }</p><p>Ol {client_name}, </p><p>Voc agora  Client ..</p><p>E-mail: {client_email} </p><p>Senha: {client_password}</p><p>{app_url}</p><p>Obrigado,</p><p>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(24, 2, 'pt', 'New Client', '<p>Ol {client_name}, </p><p>Voc agora  Client ..</p><p>E-mail: {client_email} </p><p>Senha: {client_password}</p><p>{app_url}</p><p>Obrigado,</p><p>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(25, 3, 'ar', 'New Support Ticket', '<p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\"></span><span style=\"font-size: 12pt;\">&nbsp;{support_name}</span><br><br></p><p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\">    .</span><span style=\"font-size: 12pt;\">.</span><br><br></p><p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\"></span><span style=\"font-size: 12pt;\"><strong>:</strong>&nbsp;{support_title}</span><br></p><p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\"></span><span style=\"font-size: 12pt;\"><strong>:</strong>&nbsp;{support_priority}</span><span style=\"font-size: 12pt;\"><br></span></p><p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\"> </span><span style=\"font-size: 12pt;\">: {support_end_date}</span></p><p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\"> </span><span style=\"font-size: 12pt;\"><strong>:</strong></span><br><span style=\"font-size: 12pt;\">{support_description}</span><span style=\"font-size: 12pt;\"><br><br></span></p><p><span style=\"background-color: rgb(248, 249, 250); color: rgb(34, 34, 34); font-family: inherit; font-size: 24px; text-align: right; white-space: pre-wrap;\"> </span><span style=\"font-size: 12pt;\">,</span><br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(26, 3, 'da', 'New Support Ticket', '<p><b>Hej</b>&nbsp;{support_name}<br><br></p><p>Ny supportbillet er blevet bnet.<br><br></p><p><b>Titel</b>: {support_title}<br></p><p><b>Prioritet</b>: {support_priority}<br></p><p><b>Slutdato</b>: {support_end_date}</p><p><br></p><p><b>Supportmeddelelse</b>:<br>{support_description}<br><br></p><p><b>Med venlig hilsen</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(27, 3, 'de', 'New Support Ticket', '<p><b>Hallo</b>&nbsp;{support_name}<br><br></p><p>Neues Support-Ticket wurde erffnet.<br><br></p><p><b>Titel</b>: {support_title}<br></p><p><b>Prioritt</b>: {support_priority}<br></p><p><b>Endtermin</b>: {support_end_date}</p><p><br></p><p><b>Support-Nachricht</b>:<br>{support_description}<br><br></p><p><b>Mit freundlichen Gren</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(28, 3, 'en', 'New Support Ticket', '<p><span style=\"font-size: 12pt;\"><b>Hi</b>&nbsp;{support_name}</span><br><br><span style=\"font-size: 12pt;\">New support ticket has been opened.</span><br><br><span style=\"font-size: 12pt;\"><strong>Title:</strong>&nbsp;{support_title}</span><br><span style=\"font-size: 12pt;\"><strong>Priority:</strong>&nbsp;{support_priority}</span><span style=\"font-size: 12pt;\"><br></span><span style=\"font-size: 12pt;\"><b>End Date</b>: {support_end_date}</span></p><p><br><span style=\"font-size: 12pt;\"><strong>Support message:</strong></span><br><span style=\"font-size: 12pt;\">{support_description}</span><span style=\"font-size: 12pt;\"><br><br><b>Kind Regards</b>,</span><br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(29, 3, 'es', 'New Support Ticket', '<p><b>Hola</b>&nbsp;{support_name}<br><br></p><p>Se ha abierto un nuevo ticket de soporte.<br><br></p><p><b>Ttulo</b>: {support_title}<br></p><p><b>Prioridad</b>: {support_priority}<br></p><p><b>Fecha final</b>: {support_end_date}</p><p><br></p><p><b>Mensaje de apoyo</b>:<br>{support_description}<br><br></p><p><b>Saludos cordiales</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(30, 3, 'fr', 'New Support Ticket', '<p><b>salut</b>&nbsp;{support_name}<br><br></p><p>Un nouveau ticket d\'assistance a t ouvert.<br><br></p><p><b>Titre</b>: {support_title}<br></p><p><b>Priorit</b>: {support_priority}<br></p><p><b>Date de fin</b>: {support_end_date}</p><p><b>Message d\'assistance</b>:<br>{support_description}<br><br></p><p><b>Sincres amitis</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(31, 3, 'it', 'New Support Ticket', '<p><b>Ciao</b>&nbsp;{support_name},<br><br></p><p> stato aperto un nuovo ticket di supporto.<br><br></p><p><b>Titolo</b>: {support_title}<br></p><p><b>Priorit</b>: {support_priority}<br></p><p><b>Data di fine</b>: {support_end_date}</p><p><br></p><p><b>Messaggio di supporto</b>:<br>{support_description}</p><p><b>Cordiali saluti</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(32, 3, 'ja', 'New Support Ticket', '<p> {support_name}<br><br></p><p>.<br><br></p><p>: {support_title}<br></p><p>: {support_priority}<br></p><p>: {support_end_date}</p><p><br></p><p>:<br>{support_description}<br><br></p><div class=\"tw-ta-container hide-focus-ring tw-lfl focus-visible\" id=\"tw-target-text-container\" tabindex=\"0\" data-focus-visible-added=\"\" style=\"overflow: hidden; position: relative; outline: 0px;\"><pre class=\"tw-data-text tw-text-large XcVN5d tw-ta\" data-placeholder=\"Translation\" id=\"tw-target-text\" dir=\"ltr\" style=\"unicode-bidi: isolate; line-height: 32px; border: none; padding: 2px 0.14em 2px 0px; position: relative; margin-top: -2px; margin-bottom: -2px; resize: none; overflow: hidden; width: 277px; overflow-wrap: break-word;\"><span lang=\"ja\"></span>,</pre></div><p>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(33, 3, 'nl', 'New Support Ticket', '<p><b>Hoi</b>&nbsp;{support_name}<br><br></p><p>Er is een nieuw supportticket geopend.<br><br></p><p><b>Titel</b>: {support_title}<br></p><p><b>Prioriteit</b>: {support_priority}<br></p><p><b>Einddatum</b>: {support_end_date}</p><p><br></p><p><b>Ondersteuningsbericht</b>:<br>{support_description}<br><br></p><p><b>Vriendelijke groeten</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(34, 3, 'pl', 'New Support Ticket', '<p><b>cze</b>&nbsp;{support_name}<br><br></p><p>Nowe zgoszenie do pomocy technicznej zostao otwarte.<br><br></p><p><b>Tytu</b>: {support_title}<br></p><p><b>Priorytet</b>: {support_priority}<br></p><p><b>Data kocowa</b>: {support_end_date}</p><p><br></p><p><b>Wiadomo pomocy</b>:<br>{support_description}<br><br></p><p><b>Z powaaniem</b>,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
