(57, 5, 'nl', 'Deal Assigned', '<p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\">Hallo,</span><br style=\"font-family: sans-serif;\"><span style=\"font-family: sans-serif;\">New Deal is aan u toegewezen.</span></p><p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\"><span style=\"font-weight: bolder;\">Dealnaam</span>&nbsp;: {deal_name}<br><span style=\"font-weight: bolder;\">Deal Pipeline</span>&nbsp;: {deal_pipeline}<br><span style=\"font-weight: bolder;\">Deal Stage</span>&nbsp;: {deal_stage}<br><span style=\"font-weight: bolder;\">Dealstatus</span>&nbsp;: {deal_status}<br><span style=\"font-weight: bolder;\">Deal prijs</span>&nbsp;: {deal_price}</span></p><p></p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(58, 5, 'pl', 'Deal Assigned', '<p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\">Witaj,</span><br style=\"font-family: sans-serif;\"><span style=\"font-family: sans-serif;\">Umowa zostaa przeniesiona {deal_old_stage} do&nbsp; {deal_new_stage}.</span></p><p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\"><span style=\"font-weight: bolder;\">Nazwa oferty</span>&nbsp;: {deal_name}<br><span style=\"font-weight: bolder;\">Deal Pipeline</span>&nbsp;: {deal_pipeline}<br><span style=\"font-weight: bolder;\">Etap transakcji</span>&nbsp;: {deal_stage}<br><span style=\"font-weight: bolder;\">Status oferty</span>&nbsp;: {deal_status}<br><span style=\"font-weight: bolder;\">Cena oferty</span>&nbsp;: {deal_price}</span></p><p></p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(59, 5, 'ru', 'Deal Assigned', '<p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\">,</span><br style=\"font-family: sans-serif;\"><span style=\"font-family: sans-serif;\">    {deal_old_stage} &nbsp; {deal_new_stage}.</span></p><p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\"><span style=\"font-weight: bolder;\"> </span>&nbsp;: {deal_name}<br><span style=\"font-weight: bolder;\"> </span>&nbsp;: {deal_pipeline}<br><span style=\"font-weight: bolder;\"> </span>&nbsp;: {deal_stage}<br><span style=\"font-weight: bolder;\"> </span>&nbsp;: {deal_status}<br><span style=\"font-weight: bolder;\"> </span>&nbsp;: {deal_price}</span></p><p></p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(60, 5, 'pt', 'Deal Assigned', '<p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\">Ol,</span><br style=\"font-family: sans-serif;\"><span style=\"font-family: sans-serif;\">    {deal_old_stage} &nbsp; {deal_new_stage}.</span></p><p style=\"line-height: 28px; font-family: Nunito, &quot;Segoe UI&quot;, arial; font-size: 14px;\"><span style=\"font-family: sans-serif;\"><span style=\"font-weight: bolder;\">Nome do negcio</span>&nbsp;: {deal_name}<br><span style=\"font-weight: bolder;\">Pipeline de negcios</span>&nbsp;: {deal_pipeline}<br><span style=\"font-weight: bolder;\">Estgio do negcio</span>&nbsp;: {deal_stage}<br><span style=\"font-weight: bolder;\">Status da transao</span>&nbsp;: {deal_status}<br><span style=\"font-weight: bolder;\">Preo da oferta</span>&nbsp;: {deal_price}</span></p><p></p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(61, 6, 'ar', 'New Award', '<p>&nbsp;<br>   {app_name}.</p><p><b>  </b>: {email}<br><b> </b> : {password}</p><p>{app_url}</p><p><br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(62, 6, 'da', 'New Award', '<p>Hej,&nbsp;<br>Velkommen til {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Adgangskode</b> : {password}</p><p>{app_url}</p><p>Tak,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(63, 6, 'de', 'New Award', '<p>Hallo,&nbsp;<br>Willkommen zu {app_name}.</p><p><b>Email </b>: {email}<br><b>Passwort</b> : {password}</p><p>{app_url}</p><p>Vielen Dank,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(64, 6, 'en', 'New Award', '<p>Hi ,<span style=\"font-family: var(--bs-body-font-family); font-size: var(--bs-body-font-size); font-weight: var(--bs-body-font-weight); text-align: var(--bs-body-text-align);\">{award_name}</span></p><p>I am much pleased to nominate .</p><p>I am satisfied that he/she is the best employee for the award. </p><p>I have realized that he/she is a goal-oriented person, efficient and very punctual .</p><p>Feel free to reach out if you have any question.<br></p><p>Thank You,</p><p>{app_name}</p><p>{app_url}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(65, 6, 'es', 'New Award', '<p>Hola,&nbsp;<br>Bienvenido a {app_name}.</p><p><b>Correo electrnico </b>: {email}<br><b>Contrasea</b> : {password}</p><p>{app_url}</p><p>Gracias,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(66, 6, 'fr', 'New Award', '<p>Bonjour,&nbsp;<br>Bienvenue  {app_name}.</p><p><b>Email </b>: {email}<br><b>Mot de passe</b> : {password}</p><p>{app_url}</p><p>Merci,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
(67, 6, 'it', 'New Award', '<p>Ciao,&nbsp;<br>Benvenuto a {app_name}.</p><p><b>E-mail </b>: {email}<br><b>Parola d\'ordine</b> : {password}</p><p>{app_url}</p><p>Grazie,<br>{app_name}</p>', '2022-12-02 22:23:41', '2022-12-02 22:23:41'),
