(36, 'nl', '<h3 style=\"text-align: center;\">Ervaringsbrief</h3>\r\n\r\n\r\n            <p>{app_name}</p>\r\n\r\n            <p>VOOR WIE HET AANGAAT</p>\r\n\r\n            <p>{date}</p>\r\n\r\n            <p>{employee_name}</p>\r\n\r\n            <p>Diensttijd {duration} in {app_name}.</p>\r\n\r\n            <p>{designation}</p>\r\n\r\n            <p>{payroll}</p>\r\n\r\n            <p>Rollen en verantwoordelijkheden</p>\r\n\r\n\r\n\r\n            <p>Korte omschrijving van het dienstverband van de medewerker en een positieve verklaring van de leidinggevende of leidinggevende.</p>\r\n\r\n\r\n\r\n            <p>Eerlijk,</p>\r\n\r\n            <p>{employee_name}</p>\r\n\r\n            <p>{designation}</p>\r\n\r\n            <p>Handtekening</p>\r\n\r\n            <p>{app_name}</p>', 6, '2022-12-03 03:44:48', '2022-12-03 03:44:48'),
